import React, {} from 'react';

type BtnProps = {
    currentNum: number
}

// 需要類型
const Btn: React.FC<BtnProps> = ({currentNum}) => {
    function counter() {
        currentNum++
    }
    return (
        <button onClick={counter}>
            加1，
            <span>目前總數：{currentNum}</span>
        </button>
    )
}
// props、state 應用
const App: React.FC = () => {
    const num = 0
    return <>
        <h1>計數器：{num}</h1>
        <Btn currentNum={num} />
    </>
}

export default App;
