import React from 'react'

// 1. props state
// 2. 生命週期
// 3. this

type BtnProps = {
    clickHandler: () => void
}
type BtnState = {}

class Btn extends React.Component<BtnProps,BtnState>{
    constructor( props: BtnProps ){
        super(props)
    }


    render(): React.ReactNode{
        return <button onClick={this.props.clickHandler}>+1</button>
    }
}

type AppProps = {}
type AppState = {
    count: number
}

class App extends React.Component<AppProps,AppState>{
    constructor( props: AppProps ){
        super(props)
        this.state = {
            count:0
        }
    }

    countClickHandler() {
        // 類似 setState
        
    }

    // 生命週期
    // component 安裝
    componentsDidMount() {
        console.log('畫面第一次渲染');
        this.setState( () =>{
            
        })
    }

    // component 更新時
    componentDidUpdate() {
        console.log('畫面更新');
    }

    // component 將要解除時
    componentWillUnmount() {
        console.log('畫面解除');
    }

    render(): React.ReactNode {
        return <>
            <h1>Count: {this.state.count}</h1>
            <Btn clickHandler={this.countClickHandler}/>
        </>
    }

}

export default App
